$(function () {
    var t = [];
    $("table#da-ex-datatable thead th").each(function () {
        $(this).hasClass("no_sort") ? t.push({bSortable: !1}) : t.push(null)
    }), $("table#da-ex-datatable").dataTable({sPaginationType: "two_button", aaSorting: [
        [1, "asc"]
    ], aoColumns: t}), $("#check_all_item").click(function () {
        "checked" == $(this).attr("checked") ? $(".check_item").attr("checked", "checked") : $(".check_item").removeAttr("checked")
    })
});